import React, { useState } from "react";

const CommentForm = (props) => {
  const initialState = {
    name: "",
    comment: "",
    set: false,
  };
  const [commentsData, setCommentsData] = useState(initialState);
  const handleOnChange = async (e) => {
    const { name, value } = e.target;

    setCommentsData((input) => ({ ...input, [name]: value }));
    console.log("commentsData" + commentsData.name);
    console.log(commentsData.comment);
  };

  const handleOnSubmit = (e) => {
    e.preventDefault();
    setCommentsData({ set: true });
    alert("name:" + commentsData.name + "comment:" + commentsData.comment);
  };
  return (
    <div>
      <label>Name</label>
      <input name="name" value={commentsData.name} onChange={handleOnChange} />
      <label>Comment</label>
      <input
        name="comment"
        value={commentsData.comment}
        onChange={handleOnChange}
      />
      <button onClick={handleOnSubmit}>Add</button>

      <ul>
        <li>{commentsData.name}</li>
        <li> {commentsData.comment}</li>
      </ul>
    </div>
  );
};
export default CommentForm;
