import React from "react";

const Like = (props) => {
    const {like, name}=props;

 handleOnClick= () =>{
    fetch(`/api/articles/:${name}/likes`, {
        method: 'post',
        headers: {'Content-Type':'application/json'},
        body: {
         like: this.firstName.value
        }
       });
 }
  return (
  <div>
      <button onClick={handleOnClick}>Like</button>
  </div>
  );
};
export default Like;
