import React from "react";

const CommentForm = (props) => {
  const { commentsData, setCommentsData } = props;
  const handleOnChange = async (e) => {
    const {name, value} = e.target;

    setCommentsData(input=>(
      {...input,[name]:value}
    ));
    console.log("commentsData"+commentsData.name)
    console.log(commentsData.comment)
  };


  const handleOnSubmit= e=>{
    e.preventDefault();
    alert("name:"+commentsData.name +"comment:"+commentsData.comment)
  }
  return (
    <div>
      <label>Name</label>
      <input name="name" value={commentsData.name} onChange={handleOnChange} />
      <label>Comment</label>
      <input name="comment" value={commentsData.comment} onChange={handleOnChange} />
      <button onClick={handleOnSubmit}>Add</button>
      {commentsData.name, commentsData.comment}
    </div>
  );
};
export default CommentForm;
