import React, {useState, useEffect} from "react";
import articles from "./Content";
import { Link } from "react-router-dom";
import ArticleList from "../components/ArticleList";

const Articles = ({ match }) => {
  const name = match.params.name;

  const [articleInfo, setArticleInfo] = useState({ upvotes: 0, comments: [] });
  //fetching url name/handle from the articles Route path



  useEffect(() => {
    const fetchData = async () => {
        const result = await fetch(`http://localhost:5000/api/articles/${name}`, {
          headers : { 
            'Content-Type': 'application/json',
            'Accept': 'application/json',

           }

        });
        const body = await result.json();
        console.log("body"+body);
        setArticleInfo(body);
    }
    fetchData();
}, [name]);
fetch(`http://localhost:5000/api/articles/${name}`, {
          headers : { 
            'Content-Type': 'application/json',
            'Accept': 'application/json',

           }

        })
.then(res => res.json())
.then(
  (result) => {
    this.setState({
      isLoaded: true,
      items: result.items
    });
  },
  // Note: it's important to handle errors here
  // instead of a catch() block so that we don't swallow
  // exceptions from actual bugs in components.
  (error) => {
    this.setState({
      isLoaded: true,
      error
    });
  }
)

  console.log("after use effect"+articleInfo.upvotes);
  //Return name if url path matches the name of the article
  const foundArticle = articles.find((article) => article.name === name);
  //display articles except the current one
  const otherArticles = articles.filter((article) => article.name !== name);

  return (
    <div>
      {foundArticle ? (
        <div>
          <p>Total likes: {articleInfo.upvotes}</p>
          <h2>{foundArticle.title}</h2>
          <p>{foundArticle.content}</p>

          <Link className="btn" to="/articles-all">
            All blog
          </Link>
        </div>
      ) : (
        <h4>Not found</h4>
      )}
      <ArticleList articles={otherArticles} />
    </div>
  );
};
export default Articles;
