import React, { useState } from "react";

const CommentForm = (props) => {
  const { name } = props;
  const [userData, setUserData] = useState({ name: "", comment: "" });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value });
  };

  const handleOnSubmit = (e) => {
    e.preventDefault();
    let headers = new Headers({
      "Content-Type": "application/json",
      Accept: "application/json",
    });
    alert(userData.name + userData.comment);
    if (userData.name.length > 0 && userData.comment.length > 2) {
      fetch(`/api/articles/${name}/comment`, {
        method: 'post',
        headers: {'Content-Type':'application/json'},
        body: JSON.stringify({
             "first_name": this.state.firstName
        })
     });
    }
  };
  return (
    <div>
      <label>Name</label>
      <input name="name" value={userData.name} onChange={handleChange} />
      <label>Comment</label>
      <input name="comment" value={userData.comment} onChange={handleChange} />
      <button onClick={handleOnSubmit}>Add</button>
    </div>
  );
};
export default CommentForm;
