import React from "react";

const Like = (props) => {


 const handleOnClick=async (props) =>{
     const { foundArticle, setArticleInfo}=props;
     const result = await fetch(`/api/articles/${foundArticle}/likes`, {
        method: 'post',
    });
    const body = await result.json();
    setArticleInfo(body);
    fetch(`/api/articles/${foundArticle}/likes`,{
        method: 'POST',
        headers: {
            Accept: 'application/json',
                    'Content-Type': 'application/json',
        }
    }).then(response => {
        setArticleInfo(response)
        })
        .catch(error =>{
            console.log(error)
        })

 }
  return (
  <div>
      <button onClick={()=> handleOnClick()}>Like</button>
  </div>
  );
};
export default Like;
