import React, { useState } from "react";

const CommentForm = (props) => {
  const initialState={
    name:'',
    comment:''
  }
  const [userName, setUsername] = useState(initialState);
  const [comments, setComments] = useState("");
  const handleOnChangeUsername = async (e) => {
    const { value } = e.target;
    setUsername(value);
  };
  const handleOnChangeComment = (e) => {
    const { value } = e.target;
    setComments(value);
  };
  const handleChange = e =>{
    const {name, value}=e.target;
    setUsername({...initialState,[name]:value})
  }

  const handleOnSubmit = (e) => {
    e.preventDefault();
  };
  return (
    <div>
      <label>Name</label>
      <input name="name" value={userName} onChange={handleChange} />
      <label>Comment</label>
      <input name="comment" value={comments} onChange={handleChange} />
      <button onClick={handleOnSubmit}>Add</button>
    </div>
  );
};
export default CommentForm;
