import React from "react";

const CommentForm = (props) => {
  const { comments, setArticleInfo } = props;
  const handleOnChange = async (e) => {
    const value = e.target.value;

    setArticleInfo({ [e.target.name]: value });
  };
  articleInfo
  return (
    <div>
      <label>Name</label>
      <input name="name" value={comments.user} onChange={handleOnChange} />
      <label>Comment</label>
      <input name="comment" value={comments.text} onChange={handleOnChange} />
      <button>Add</button>
    </div>
  );
};
export default CommentForm;
