import React from "react";
import articles from "./Content";

const Articles = ({ match }) => {
  const name = match.params.name;
  const foundArticle = articles.find(article=>article.name===name)

  console.log("Found article name" + foundArticle);
  const data = (foundArticle?articles.map((data, key) => {
    const { name, title, content } = data;
    if(foundArticle===name){
    return (
      <ul>
        <li key={key}>{title}</li>
        <li>{content}</li>
      </ul>
    );
    }
  }):'Not Found')
  return (
    <div>
      Articles {name}
      {data}
    </div>
  );
};
export default Articles;
