import React, {useState, useEffect} from "react";
import articles from "./Content";
import { Link } from "react-router-dom";
import ArticleList from "../components/ArticleList";

const Articles = ({ match }) => {

  const [articlesInfo, setarticlesInfo]= useState({upvotes:0, comments:[]});
  //fetching url name/handle from the articles Route path

  const name = match.params.name;

  useEffect(() => {
      const fetchData = async () => {
          const result = await fetch(`http://localhost:5000/api/articles/${name}`);
          const body = await result.json();
          console.log(body);
          setArticleInfo(body);
      }
      fetchData();
  }, [name]);

  console.log("after use effect"+articlesInfo.name);
  //Return name if url path matches the name of the article
  const foundArticle = articles.find((article) => article.name === name);
  //display articles except the current one
  const otherArticles = articles.filter((article) => article.name !== name);

  return (
    <div>
      {foundArticle ? (
        <div>
          <p>Total likes: {articlesInfo.upvotes}</p>
          <h2>{foundArticle.title}</h2>
          <p>{foundArticle.content}</p>

          <Link className="btn" to="/articles-all">
            All blog
          </Link>
        </div>
      ) : (
        <h4>Not found</h4>
      )}
      <ArticleList articles={otherArticles} />
    </div>
  );
};
export default Articles;
