import React, { useState } from "react";

const CommentForm = (props) => {
  const [userData, setUserData] = useState({ name: "", comment: ""});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value });
  };

  const handleOnSubmit = (e) => {
    e.preventDefault();
    alert(userName.name+userName.comment)
  };
  return (
    <div>
      <label>Name</label>
      <input name="name" value={userName.name} onChange={handleChange} />
      <label>Comment</label>
      <input name="comment" value={userName.comment} onChange={handleChange} />
      <button onClick={handleOnSubmit}>Add</button>
    </div>
  );
};
export default CommentForm;
