const Like = (props) => {


 const handleOnClick=async (props) =>{
     const { foundArticle, setArticleInfo}=props;

    fetch(`/api/articles/${foundArticle}/likes`,{ method: 'POST',

    }).then((res) => res.json())
    .then(response => {
        setArticleInfo(response)
        })
        .catch(error =>{
            console.log(error)
        })

 }
  return (
  <div>
      <button onClick={()=> handleOnClick()}>Like</button>
  </div>
  );
};
