import React from 'react';

const CommentsList = (props) => (
    <>
    <h3>Comments:</h3>
    {props.comments&&props.comments.map((comment, key) => (
        <div className="comment" key={key}>
            <h4>{comment.user}</h4>
            <p>{comment.text}</p>
        </div>
    ))}
    </>
);

export default CommentsList;
