import React from "react";
import articles from "./Content";
import { Link } from "react-router-dom";

const ArticlesList = () => {
  const allArticles = articles.map((articles, key) => {
    const { title, content, name } = articles;
    let trimmedContent, readMore;
    if(content[key].length>500){
     trimmedContent=content[key].slice(0,500)
     readMore=
          <Link to={`/articles/${name}`}>
          Read More
        </Link>

    }
    return (
      <div key={key} >
        <Link to={`/articles/${name}`}>
          {title}
        </Link>
        <p>{trimmedContent}..
        {readMore}
        </p>
      </div>
    );
  });
  return <div>{allArticles}</div>;
};
export default ArticlesList;
