import React from "react";

const Like = (props) => {
  const handleOnClick = async (props) => {
    const { likes, foundArticle, setArticleInfo } = props;

    const result = fetch(`/api/articles/${foundArticle}/likes`, {
      method: "POST",
    });
    const body = result.json();
    setArticleInfo(body);
  };
  return (
    <div>
        <p>Total likes: {likes}</p>
      <button onClick={() => handleOnClick()}>Like</button>
    </div>
  );
};
export default Like;
