import React from 'react'

 const CommentsList = (props) => {
    return (
        <div>
            {props.comments &&props.comments.map((item,i)=>{
               return (
                   <div>
               <p>{item.user}</p>
               <p>{item.text}</p>
               </div>
               )
            })}
        </div>
    )
}
export default CommentsList;
