import React, { useState } from "react";

const CommentForm = (props) => {
  const initialState = {
    name: "",
    comment: "",
  };
  const [commentsData, setCommentsData] = useState([initialState]);


  const handleOnSubmit = (e) => {
    e.preventDefault();
    alert("name:" + commentsData.name + "comment:" + commentsData.comment);
  };
  return (
    <div>
      <label>Name</label>
      <input name="name"  />
      <label>Comment</label>
      <input
        name="comment"

      />
      <button onClick={handleOnSubmit}>Add</button>

      <ul>
        <li>{commentsData.name}</li>
        <li> {commentsData.comment}</li>
      </ul>
    </div>
  );
};
export default CommentForm;
