import React from "react";

const Like = (props) => {
    const {like, name}=props;

 const handleOnClick=async (props) =>{
     const {likes, foundArticle, setArticleInfo}=props;
     const result = await fetch(`/api/articles/${foundArticle}/likes`, {
        method: 'post',
    });
    const body = await result.json();
    setArticleInfo(body);


 }
  return (
  <div>
      <button onClick={()=>handleOnClick}>Like</button>
  </div>
  );
};
export default Like;
